import api from '../../../api/decision';
export default {
  name: 'DecisionDetailModal',
  props: {
    record: Object,
    visible: Boolean,
    type: String,
    decision_id: Number
  },
  data: function data() {
    return {
      form: this.$form.createForm(this)
    };
  },
  mounted: function mounted() {},
  methods: {
    handleOk: function handleOk() {
      var _this = this;

      this.form.validateFields(function (error, data) {
        if (!error) {
          data.decision = _this.decision_id;

          if (_this.isEdit) {
            api.updateDecisionMatter(_this.record.matter_id, data).then(function (data) {
              _this.$emit('close');

              _this.$notification.success({
                message: '成功',
                description: '修改成功',
                key: 'SUCCESS'
              });
            });
          } else {
            api.createDecisionMatter(data, _this.decision_id).then(function (data) {
              _this.$emit('close');

              _this.$notification.success({
                message: '成功',
                description: '新建成功',
                key: 'SUCCESS'
              });
            });
          }
        }
      });
    },
    handleCancel: function handleCancel() {
      this.$emit('close');
    }
  },
  computed: {
    isEdit: function isEdit() {
      return this.type === 'edit';
    },
    title: function title() {
      return this.isEdit ? '编辑' : '新建';
    }
  }
};
