import api from '../../../../../api/implement';
import moment from 'moment';
export default {
  name: 'ProcessDetailModal',
  props: {
    record: Object,
    visible: Boolean,
    type: String,
    detail_id: Number
  },
  data: function data() {
    return {
      layout: {
        'label-col': {
          span: 8
        },
        'wrapper-col': {
          span: 12
        }
      },
      form: this.$form.createForm(this),
      details: {}
    };
  },
  mounted: function mounted() {
    this.getData();
  },
  methods: {
    getData: function getData() {
      var _this = this;

      if (this.isEdit) {
        this.record.time = new moment(this.record.time);
        api.getInvestProcessDetailMatter(this.record.id).then(function (data) {
          _this.details = data;
        });
      }
    },
    handleOk: function handleOk() {
      var _this2 = this;

      this.form.validateFields(function (error, data) {
        console.log(data);

        if (!error) {
          data.time = data.time.format('YYYY-MM-DD');

          if (_this2.isEdit) {
            api.updateInvestProcessDetail(_this2.record.id, data).then(function (data) {
              _this2.$notification.success({
                message: '成功',
                description: '更新成功',
                key: 'SUCCESS'
              });

              _this2.$emit('close');
            });
          } else {
            api.createInvestProcessDetail(_this2.detail_id, data).then(function (data) {
              console.log(_this2.detail_id);

              _this2.$notification.success({
                message: '成功',
                description: '新建成功',
                key: 'SUCCESS'
              });

              _this2.$emit('close');
            });
          }
        }
      });
    },
    handleCancel: function handleCancel() {
      this.$emit('close');
    }
  },
  computed: {
    isEdit: function isEdit() {
      return this.type === 'edit';
    },
    title: function title() {
      return this.isEdit ? '编辑' : '新建';
    }
  }
};
