import api from '../../../../api/implement';
export default {
  name: 'Design3CardModal',
  props: {
    record: Object,
    visible: Boolean,
    type: String
  },
  data: function data() {
    return {
      layout: {
        'label-col': {
          span: 8
        },
        'wrapper-col': {
          span: 12
        }
      },
      form: this.$form.createForm(this)
    };
  },
  methods: {
    handleOk: function handleOk() {
      var _this = this;

      this.form.validateFields(function (error, data) {
        if (!error) {
          if (_this.isEdit) {
            api.updateDesignCompany(_this.record.company_id, data).then(function (data) {
              _this.$notification.success({
                message: '成功',
                description: '更新成功',
                key: 'SUCCESS'
              });

              _this.$emit('close');
            });
          } else {
            api.createDesignCompany(data, _this.$store.getters.projectSelected.id).then(function (data) {
              _this.$notification.success({
                message: '成功',
                description: '新建成功',
                key: 'SUCCESS'
              });

              _this.$emit('close');
            });
          }
        }
      });
    },
    handleCancel: function handleCancel() {
      this.$emit('close');
    }
  },
  computed: {
    isEdit: function isEdit() {
      return this.type === 'edit';
    },
    title: function title() {
      return this.isEdit ? '编辑' : '新建';
    }
  }
};
