import api from '../../../../api/implement';
export default {
  name: 'ApplicationDetailModal',
  props: {
    record: Object,
    visible: Boolean,
    type: String,
    application_id: Number
  },
  data: function data() {
    return {
      layout: {
        'label-col': {
          span: 8
        },
        'wrapper-col': {
          span: 12
        }
      },
      form: this.$form.createForm(this),
      matter: {}
    };
  },
  mounted: function mounted() {
    this.getData();
  },
  methods: {
    getData: function getData() {
      var _this = this;

      if (this.isEdit) {
        api.getApplicationMatter(this.record.matter_id).then(function (data) {
          _this.matter = data;
        });
      }
    },
    handleOk: function handleOk() {
      var _this2 = this;

      this.form.validateFields(function (error, data) {
        if (!error) {
          if (_this2.isEdit) {
            api.updateApplicationMatter(_this2.record.matter_id, data).then(function (data) {
              _this2.$notification.success({
                message: '成功',
                description: '更新成功',
                key: 'SUCCESS'
              });

              _this2.$emit('close');
            });
          } else {
            api.createApplicationMatter(_this2.application_id, data).then(function (data) {
              _this2.$notification.success({
                message: '成功',
                description: '新建成功',
                key: 'SUCCESS'
              });

              _this2.$emit('close');
            });
          }
        }
      });
    },
    handleCancel: function handleCancel() {
      this.$emit('close');
    }
  },
  computed: {
    isEdit: function isEdit() {
      return this.type === 'edit';
    },
    title: function title() {
      return this.isEdit ? '编辑' : '新建';
    }
  }
};
