import api from '../../../../api/scheduleControl';
import moment from 'moment';
export default {
  name: 'ScheduleNoticeModal',
  props: {
    record: Object,
    visible: Boolean,
    type: String,
    notice_id: Number,
    id: Number
  },
  data: function data() {
    return {
      layout: {
        'label-col': {
          span: 8
        },
        'wrapper-col': {
          span: 12
        }
      },
      form: this.$form.createForm(this),
      notice: {}
    };
  },
  mounted: function mounted() {
    this.getData();
  },
  methods: {
    getData: function getData() {
      var _this = this;

      if (this.isEdit) {
        this.record.give_time = new moment(this.record.give_time);
        this.record.reply_time = new moment(this.record.reply_time);
        api.getScheduleNoticeMatter(this.record.id).then(function (data) {
          _this.notice = data;
        });
      }
    },
    handleOk: function handleOk() {
      var _this2 = this;

      this.form.validateFields(function (error, data) {
        console.log(data);

        if (!error) {
          data.give_time = data.give_time.format('YYYY-MM-DD');

          if (data.reply_time != null) {
            data.reply_time = data.reply_time.format('YYYY-MM-DD');
          }

          if (_this2.isEdit) {
            api.updateScheduleNotice(_this2.record.id, data).then(function (data) {
              _this2.$notification.success({
                message: '成功',
                description: '更新成功',
                key: 'SUCCESS'
              });

              _this2.$emit('close');
            });
          } else {
            api.createScheduleNotice(_this2.$store.getters.projectSelected.id, data).then(function (data) {
              _this2.$notification.success({
                message: '成功',
                description: '新建成功',
                key: 'SUCCESS'
              });

              _this2.$emit('close');
            });
          }
        }
      });
    },
    handleCancel: function handleCancel() {
      this.$emit('close');
    }
  },
  computed: {
    isEdit: function isEdit() {
      return this.type === 'edit';
    },
    title: function title() {
      return this.isEdit ? '编辑' : '新建';
    }
  }
};
