import api from '../../../../api/implement';
export default {
  name: 'InformationModal',
  props: {
    record: Object,
    visible: Boolean,
    type: String
  },
  data: function data() {
    return {
      form: this.$form.createForm(this)
    };
  },
  methods: {
    handleOk: function handleOk() {
      var _this = this;

      this.form.validateFields(function (error, data) {
        if (!error) {
          if (_this.isEdit) {
            api.updateInformation(_this.record.information_id, data).then(function (data) {
              _this.$emit('close');

              _this.$notification.success({
                message: '成功',
                description: '更新成功',
                key: 'SUCCESS'
              });
            });
          } else {
            data.project = _this.$store.getters.projectSelected.id;
            api.createInformation(_this.$store.getters.projectSelected.id, data).then(function (data) {
              _this.$emit('close');

              _this.$notification.success({
                message: '成功',
                description: '新建成功',
                key: 'SUCCESS'
              });
            });
          }
        }
      });
    },
    handleCancel: function handleCancel() {
      this.$emit('close');
    }
  },
  computed: {
    isEdit: function isEdit() {
      return this.type === 'edit';
    },
    title: function title() {
      return this.isEdit ? '编辑' : '新建';
    }
  }
};
