import api from '../../../../api/implement';
import moment from 'moment';
export default {
  name: 'payDetailModal',
  props: {
    record: Object,
    visible: Boolean,
    type: String,
    company_id: Number
  },
  data: function data() {
    return {
      layout: {
        'label-col': {
          span: 8
        },
        'wrapper-col': {
          span: 12
        }
      },
      form: this.$form.createForm(this)
    };
  },
  mounted: function mounted() {
    this.getData();
  },
  methods: {
    getData: function getData() {
      if (this.isEdit) {
        this.record.time = new moment(this.record.time);
      }
    },
    handleOk: function handleOk() {
      var _this = this;

      this.form.validateFields(function (error, data) {
        console.log(data);

        if (!error) {
          data.time = data.time.format('YYYY-MM-DD');

          if (_this.isEdit) {
            api.updateDesignPayDetail(_this.record.id, data).then(function (data) {
              _this.$notification.success({
                message: '成功',
                description: '更新成功',
                key: 'SUCCESS'
              });

              _this.$emit('close');
            });
          } else {
            api.createDesignPayDetail(_this.company_id, data).then(function (data) {
              _this.$notification.success({
                message: '成功',
                description: '新建成功',
                key: 'SUCCESS'
              });

              _this.$emit('close');
            });
          }
        }
      });
    },
    handleCancel: function handleCancel() {
      this.$emit('close');
    }
  },
  computed: {
    isEdit: function isEdit() {
      return this.type === 'edit';
    },
    title: function title() {
      return this.isEdit ? '编辑' : '新建';
    }
  }
};
