import api from '../../../api/decision';
export default {
  name: 'DecisionModal',
  props: {
    record: Object,
    visible: Boolean,
    type: String
  },
  data: function data() {
    return {
      form: this.$form.createForm(this)
    };
  },
  methods: {
    handleOk: function handleOk() {
      var _this = this;

      console.log('OK');
      this.form.validateFields(function (error, data) {
        console.log('OK');

        if (!error) {
          console.log('OK');

          if (_this.isEdit) {
            console.log(data);
            api.updateDecision(_this.record.decision_id, data).then(function (data) {
              _this.$emit('close');

              console.log('update');
              console.log(data);

              _this.$notification.success({
                message: '成功',
                description: '更新成功',
                key: 'SUCCESS'
              });
            });
          } else {
            api.createDecision(_this.$store.getters.projectSelected.id, data).then(function (data) {
              _this.$emit('close');

              _this.$notification.success({
                message: '成功',
                description: '新建成功',
                key: 'SUCCESS'
              });
            });
          }
        }
      });
    },
    handleCancel: function handleCancel() {
      this.$emit('close');
    }
  },
  computed: {
    isEdit: function isEdit() {
      return this.type === 'edit';
    },
    title: function title() {
      return this.isEdit ? '编辑' : '新建';
    }
  }
};
