import api from '../../../../api/implement'
export default {
  name: 'DesignMoneyModal',
  props: {
    record: Object,
    visible: Boolean,
    type: String,
    money_id: String
  },
  data () {
    return {
      layout: {
        'label-col': { span: 8 },
        'wrapper-col': { span: 12 }
      },
      form: this.$form.createForm(this),
     moneys: {},
    }
  },
  mounted () {
    this.getData()
  },
  methods: {
    getData () {
      if (this.isEdit) {
        api. getDesignCompanyMatter(this.record.id)
          .then(data => {
            this.moneys = data
          })
      }
    },
    handleOk () {
      this.form.validateFields((error, data) => {
        console.log(data)
        if (!error) {
          if (this.isEdit) {
            api.updateDesignCompanyMatter(this.record.money_id, data)
              .then(data => {
                this.$notification.success({message: '成功', description: '更新成功', key: 'SUCCESS'})
                this.$emit('close')
              })
          } else {
            api.createDesignCompanyMatter(this.$store.getters.projectSelected.id, data)
              .then(data => {
                this.$notification.success({message: '成功', description: '新建成功', key: 'SUCCESS'})
                this.$emit('close')
              })
          }
        }
      })
    },
    handleCancel () {
      this.$emit('close')
    }

  },
  computed: {
    isEdit () {
      return this.type === 'edit'
    },
    title () {
      return this.isEdit ? '编辑' : '新建'
    }
  }
}
