import api from '../../../../api/implement'
import moment from 'moment'
export default {
  name: 'payDetailModal',
  props: {
    record: Object,
    visible: Boolean,
    type: String,
    company_id: Number,
  },
  data () {
    return {
      layout: {
        'label-col': { span: 8 },
        'wrapper-col': { span: 12 }
      },
      form: this.$form.createForm(this)
    }
  },
  mounted () {
    this.getData()
  },
  methods: {
    getData () {
      if (this.isEdit) {
        this.record.time=new moment(this.record.time)
      }
    },
    handleOk () {
      this.form.validateFields((error, data) => {
        console.log(data)
        if (!error) {
          data.time=data.time.format('YYYY-MM-DD')
          if (this.isEdit) {
            api.updateDesignPayDetail(this.record.id, data)
              .then(data => {
                this.$notification.success({message: '成功', description: '更新成功', key: 'SUCCESS'})
                this.$emit('close')
              })
          } else {
            api.createDesignPayDetail(this.company_id, data)
              .then(data => {
                this.$notification.success({message: '成功', description: '新建成功', key: 'SUCCESS'})
                this.$emit('close')
              })
          }
        }
      })
    },
    handleCancel () {
      this.$emit('close')
    }

  },
  computed: {
    isEdit () {
      return this.type === 'edit'
    },
    title () {
      return this.isEdit ? '编辑' : '新建'
    }
  }
}
