import api from '../../../api/decision'

export default {
  name: 'DecisionModal',
  props: {
    record: Object,
    visible: Boolean,
    type: String
  },
  data () {
    return {
      form: this.$form.createForm(this)
    }
  },
  methods: {
    handleOk () {
      console.log('OK')
      this.form.validateFields((error, data) => {
        console.log('OK')
        if (!error) {
          console.log('OK')
          if (this.isEdit) {
            console.log(data)
            api.updateDecision(this.record.decision_id, data)
              .then(data => {
                this.$emit('close')
                console.log('update')
                console.log(data)
                this.$notification.success({ message: '成功', description: '更新成功', key: 'SUCCESS' })
              })
          } else {
            api.createDecision(this.$store.getters.projectSelected.id, data)
              .then(data => {
                this.$emit('close')
                this.$notification.success({ message: '成功', description: '新建成功', key: 'SUCCESS' })
              })
          }
        }
      })
    },
    handleCancel () {
      this.$emit('close')
    }
  },
  computed: {
    isEdit () {
      return this.type === 'edit'
    },
    title () {
      return this.isEdit ? '编辑' : '新建'
    }
  }
}
