import api from '../../../../api/setting'
import AFormItem from 'ant-design-vue/es/form/FormItem'
import {mapGetters} from 'vuex'

export default {
  name: 'userModal',
  components: {AFormItem},
  props: {
    record: Object,
    visible: Boolean,
    type: String,
    users_id: Number
  },
  data() {
    return {
      layout: {
        'label-col': {span: 8},
        'wrapper-col': {span: 12}
      },
      form: this.$form.createForm(this),
      users: {},
      rolesList: []
    }
  },
  mounted() {
    this.getData()
  },
  methods: {
    getData() {
      api.getSettingRoles().then(data => {
        console.log(data)
        this.rolesList = data
      })
      if (this.isEdit) {
        api.getSettingUsersMatter(this.record.id)
          .then(data => {
            this.users = data
          })
      }
    },
    handleOk() {
      this.form.validateFields((error, data) => {
        if (!error) {
           var result = []
          console.log(data.role)
          for (var temp of this.rolesList) {
            if (temp.name == data.role) {
              console.log(temp.name)
              result.push(temp)
            }
          }
          console.log(result[0])
          data.role = result[0]
          if (this.isEdit) {

            api.updateSettingUsersMatter(this.record.id, data)
              .then(data => {
                this.$notification.success({message: '成功', description: '更新成功', key: 'SUCCESS'})
                this.$emit('close')
              })
          } else {
             // var te=null
             //    for (var temp of this.rolesList) {
             //      if (temp.name == data.role.name) {
             //        console.log(temp)
             //        console.log("tempid")
             //        console.log(temp.id)
             //        te=temp.id
             //      }
             //    }
             //    data.role=te
            api.createSettingUsersMatter(data)
              .then(data => {
                this.$notification.success({message: '成功', description: '新建成功', key: 'SUCCESS'})
                this.$emit('close')
              })
          }
        }
      })
    },
    handleCancel() {
      this.$emit('close')
    },
    validatePassword(rule, value, callback) {
      if (/[\u4e00-\u9fa5]/.test(value)) {
        callback('id can not contain chinese')
      } else {
        callback()
      }
    },
    validateSortPsw(rule, value, callback) {
      if (/[\u4e00-\u9fa5]/.test(value)) {
        callback('id can not contain chinese')
      } else {
        callback()
      }
    }


  },
  computed: {
    isEdit() {
      return this.type === 'edit'
    },
    title() {
      return this.isEdit ? '编辑' : '新建'
    }
  }
}
