import api from '../../../../api/implement'
import {mapGetters} from 'vuex'

export default {
  name: 'ClaimModal',
  props: {
    record: Object,
    visible: Boolean,
    type: String,
  },
  data() {
    return {
      project_id: this.projectSelected().id,
      layout: {
        'label-col': {span: 8},
        'wrapper-col': {span: 12}
      },
      form: this.$form.createForm(this),
      companyList: null,
      matter: {}
    }
  },
  mounted() {
    this.getData()
  },
  methods: {
    ...mapGetters(['projectSelected']),
    getData() {
      api.getClaimAddressList(this.project_id)
        .then(data => {
          this.companyList = data
          console.log(this.companyList)
        })
    },
    handleClaimOk() {
      this.form.validateFields((error, data) => {
        if (!error) {
          if (this.isEdit) {
            api.updateClaim(this.record.claim_id, data)
              .then(data => {
                this.$notification.success({message: '成功', description: '更新成功', key: 'SUCCESS'})
                this.$emit('close')
              })
          } else {
            api.createClaim(this.project_id, data)
              .then(data => {
                this.$notification.success({message: '成功', description: '新建成功', key: 'SUCCESS'})
                this.$emit('close')
              })
          }
        }
      })
    },
    handleClaimCancel() {
      this.$emit('close')
    },
    validateTimeLimit(rule, value, callback) {
      if (this.isEdit) {
        callback()
      } else {
        if (value < 0)
          callback('false')
        else
          callback()
      }
    }

  },
  computed: {
    isEdit() {
      return this.type === 'edit'
    },
    title() {
      return this.isEdit ? '编辑' : '新建'
    }
  }
}
