import api from '../../../../../api/supervisorQuality'
import { mapGetters } from 'vuex'

export default {
  name: 'ProcessAuditModal',
  props: {
    record: Object,
    visible: Boolean,
    type: String,
    process_review_id:Number
  },
  data () {
    return {
      project_id: this.projectSelected().id,
      layout: {
        'label-col': { span: 8 },
        'wrapper-col': { span: 12 }
      },
      form: this.$form.createForm(this),
      matter: {}
    }
  },
  methods: {
    ...mapGetters(['projectSelected']),
    handleProcessAuditOk () {
      console.log(this.matter_id)
      this.form.validateFields((error, data) => {
        if (!error) {
          if (this.isEdit) {
            api.updateProcessAuditTable(this.record.matter_id, data)
              .then(data => {
                this.$notification.success({ message: '成功', description: '更新成功', key: 'SUCCESS' })
                this.$emit('close')
              })
          } else {
            api.createProcessAuditTable(this.process_review_id, data)
              .then(data => {
                this.$notification.success({ message: '成功', description: '新建成功', key: 'SUCCESS' })
                this.$emit('close')
                console.log(this.process_review_id)
              })
          }
        }
      })
    },
    handleProcessAuditCancel () {
      this.$emit('close')
    },
    validateProgressLimit (rule, value, callback) {
      if (this.isEdit) {
        callback()
      } else {
        if (value < 0 || value > 100)
          callback('false')
        else
          callback()
      }
    }
  },
  computed: {
    isEdit () {
      return this.type === 'edit'
    },
    title () {
      return this.isEdit ? '编辑' : '新建'
    }
  }
}
