import api from '../../../api/detail'
import { mapGetters } from 'vuex'

export default {
  name: 'ContactModal',
  props: {
    record: Object,
    visible: Boolean,
    type: String
  },
  data () {
    return {
      layout: {
        'label-col': { span: 8 },
        'wrapper-col': { span: 16 }
      },
      form: this.$form.createForm(this),
      typeList: [],
      params: {
        project_id: this.projectSelected().id
      }
    }
  },
  mounted () {
    api.getTypeList()
      .then(data => {
        //console.log(data)
        this.typeList = data
      })
  },
  methods: {
    ...mapGetters(['projectSelected']),
    handleOk () {
      this.form.validateFields((error, data) => {
        if (!error) {
          data.company_type = {
            value: data.company_type
          }
          if (this.isEdit) {
            data.id = this.record.id
            api.updateAddressList(data)
              .then(data => {
                console.log(data)
                this.$emit('close')
                this.$notification.success({ message: '成功', description: '修改成功', key: 'SUCCESS' })
              })
          } else {
            api.createAddressList(data, this.params)
              .then(data => {
                this.$emit('close')
                this.$notification.success({ message: '成功', description: '新建成功', key: 'SUCCESS' })
              })
          }
        }
      })
    },
    handleCancel () {
      this.$emit('close')
    }
  },
  computed: {
    isEdit () {
      return this.type === 'edit'
    },
    title () {
      return this.isEdit ? '编辑' : '新建'
    }
  }
}
