import api from '../../../api/sale'
import {mapGetters} from 'vuex'

export default {
  name: 'CustomerInfoModal',
  props: {
    record: Object,
    visible: Boolean,
    type: String,
  },
  data() {
    return {
      //project_id: this.projectSelected().id,
      layout: {
        'label-col': {span: 8},
        'wrapper-col': {span: 12}
      },
      form: this.$form.createForm(this),
      matter: {},
      typeList: [],
    }
  },
  mounted() {
  },
  methods: {
    ...mapGetters(['projectSelected']),
    handleOk() {
      this.form.validateFields((error, data) => {
        if (!error) {
          if (this.isEdit) {
            api.updateCustomerInfo(this.record.id, data)
              .then(data => {
                this.$notification.success({message: '成功', description: '更新成功', key: 'SUCCESS'})
                this.$emit('close')
              })
          } else {
            api.createCustomerInfo(this.project_id, data)
              .then(data => {
                this.$notification.success({message: '成功', description: '新建成功', key: 'SUCCESS'})
                this.$emit('close')
              })
          }
        }
      })
    },
    handleCancel() {
      this.$emit('close')
    }

  },
  computed: {
    isEdit() {
      return this.type === 'edit'
    },
    title() {
      return this.isEdit ? '编辑' : '新建'
    }
  }
}
