import Card from '../../decision/components/Card'
import TotalCard from '../../decision/components/TotalCard'
import api from '../../../api/implement'
import echarts from 'echarts'

export default {
  name: 'Application',
  components: {
    Card,
    TotalCard
  },
  data () {
    return {
      applicationList: [],
      option1: {
        tooltip: {},
        xAxis: {
          data: ['预算', '实际', '节约'],
          axisLabel: {
            show: true,
            textStyle: {
              fontSize: 20
            }
          }
        },
        yAxis: {
          axisLabel: {
            show: true,
            textStyle: {
              fontSize: 14
            }
          }
        },
        series: [{
          label: {
            textStyle: {
              fontSize: 50
            }
          },
          name: '总计',
          type: 'bar',
          barWidth: 50,
          data: [0, 0, 0],
          itemStyle: {
            normal: {
              //每根柱子颜色设置
              color: function (params) {
                let colorList = [
                  '#32CD32',
                  '#FF0000',
                  '#FFD700'
                ]
                return colorList[params.dataIndex]
              }
            }
          }
        }]
      },
      chart: null
    }
  },
  beforeMount() {
    this.getData()
  },
  mounted () {

  },
  methods: {
    getData () {
      api.getApplicationList(this.$store.getters.projectSelected.id)
        .then(data => {
          console.log(data)
          this.applicationList = data
          this.change()
        })
    },
    change () {
      var arr = []

      this.chart = echarts.init(document.getElementById('echartContainer'))
      arr.push(this.total.estimate)
      arr.push(this.total.actual)
      arr.push(this.total.estimate - this.total.actual)
      console.log(this.total)
      console.log(arr)
      this.chart.setOption({
        tooltip: {},
        xAxis: {
          data: ['预算', '实际', '节约'],
          axisLabel: {
            show: true,
            textStyle: {
              fontSize: 20
            }
          }
        },
        yAxis: {
          axisLabel: {
            show: true,
            textStyle: {
              fontSize: 14
            }
          }
        },
        series: [{
          label: {
            textStyle: {
              fontSize: 50
            }
          },
          name: '总计',
          type: 'bar',
          barWidth: 50,
          data: arr,
          itemStyle: {
            normal: {
              //每根柱子颜色设置
              color: function (params) {
                let colorList = [
                  '#32CD32',
                  '#FF0000',
                  '#FFD700'
                ]
                return colorList[params.dataIndex]
              }
            }
          }
        }]
      }, true)
      window.onresize = this.chart.resize
    }
  },
  computed: {
    total () {
      let ret = {
        estimate: '0',
        actual: '0'
      }
      if (this.applicationList.length >0) {
        ret = {
          estimate: this.applicationList.map((item) => {
            return parseFloat(item.estimate)
          }).reduce((x, y) => {
            return x + y
          }).toFixed(2),
          actual: this.applicationList.map((item) => {
            let actual = item.actual
            if (actual) {
              return parseFloat(actual)
            } else {
              return 0
            }
          }).reduce((x, y) => {
            return x + y
          }).toFixed(2)
        }
      }
      return ret
    }
  }
}
