import echarts from 'echarts'
import BuildModal from './components/BuildModal'
import api from '../../../api/implement'
import {mapGetters} from 'vuex'

const columns = [
  {
    title: '建筑名称',
    dataIndex: 'name',
    width: '50%',
    align: 'center'
  },
  {
    title: '进度',
    dataIndex: 'progress',
    width: '25%',
    align: 'center'
  },
  {
    title: '编辑',
    dataIndex: 'operation',
    scopedSlots: {customRender: 'operation'},
    align: 'center'
  }
]

export default {
  name: 'Build',
  components: {
    BuildModal
  },
  props: {
    id: Number
  },
  data() {
    return {
      status: {
        listLoading: true,
        tableLoading: true
      },
      card: {
        save_rate: null,
        bid_price: null,
        control_price: null
      },
      option: {
        tooltip: {},
        xAxis: {
          data: ['0'],
          axisLabel: {
            show: true,
            textStyle: {
              fontSize: 20
            }
          }
        },
        yAxis: {
          axisLabel: {
            show: true,
            textStyle: {
              fontSize: 14
            }
          }
        },
        series: [{
          label: {
            textStyle: {
              fontSize: 50
            }
          },
          name: '总计',
          type: 'bar',
          barWidth: 50,
          data: [0],
          itemStyle: {
            normal: {
              //每根柱子颜色设置
              color: function (params) {
                let colorList = [
                  '#32CD32',
                  '#FF0000',
                  '#FFD700'
                ]
                return colorList[params.dataIndex]
              }
            }
          }
        }]
      },
      chart: null,
      layout: {
        'label-col': {span: 6},
        'wrapper-col': {span: 18}
      },
      form: this.$form.createForm(this),
      buildModal: {
        record: null,
        visible: false,
        type: '1',
        building_id: this.id
      },
      columns,
      matterList: [],
      project_id: this.projectSelected().id
    }
  },
  mounted() {
    this.chart = echarts.init(document.getElementById('echartContainer'))
    this.chart.setOption(this.option, true)
    window.onresize = this.chart.resize
    this.getData()
  },
  methods: {
    ...mapGetters(['projectSelected']),
    getData() {
      api.getBuildList(this.project_id)
        .then(data => {
          this.matterList = data
          this.status.listLoading = false
          this.change()
        })

    },
    handleBuildClose() {
      this.buildModal.type = ''
      this.buildModal.record = null
      this.buildModal.visible = false
      this.getData()
    },
    handleBuildCreate() {
      this.buildModal.type = 'create'
      this.buildModal.record = null
      this.buildModal.visible = true
    },
    handleBuildEdit(record) {
      this.buildModal.type = 'edit'
      this.buildModal.record = record
      this.buildModal.visible = true
    },
    handleBuildDelete(record) {
      api.deleteBuild(record.building_id)
        .then(data => {
          this.$notification.success({message: '成功', description: '删除成功', key: 'SUCCESS'})
        })
    },
    parseFloat(f) {
      return parseFloat(f)
    },
    change() {
      var arrName = []
      var arrProgress = []
      for( var building of this.matterList) {
        console.log(this.matterList)
        console.log(building)
        arrName.push(building.name)
        arrProgress.push(building.progress)
      }
      // console.log(building)
      // console.log(arrName)
      // console.log(arrProgress)
      this.chart.setOption({
        tooltip: {},
        xAxis: {
          data:arrName ,
          axisLabel: {
            show: true,
            textStyle: {
              fontSize: 20
            }
          }
        },
        yAxis: {
          axisLabel: {
            show: true,
            textStyle: {
              fontSize: 14
            }
          }
        },
        series: [{
          label: {
            textStyle: {
              fontSize: 50
            }
          },
          name: '总计',
          type: 'bar',
          barWidth: 50,
          data: arrProgress,
          itemStyle: {
            normal: {
              //每根柱子颜色设置
              color:'#1890FF'
            }
          }
        }]
      }, true)

    }
  }
}
