import api from '../../../../api/implement'

export default {
  name: 'BidModal',
  props: {
    record: Object,
    visible: Boolean,
    type: String,
    application_id: Number
  },
  data () {
    return {
      layout: {
        'label-col': { span: 8 },
        'wrapper-col': { span: 12 }
      },
      form: this.$form.createForm(this),
      matter: {},
    }
  },
  mounted () {
    this.getData()
  },
  methods: {
    getData () {
      if (this.isEdit) {
        api.getApplicationMatter(this.record.matter_id)
          .then(data => {
            this.matter = data
          })
      }
    },
    handleOk () {
      this.form.validateFields((error, data) => {
        if (!error) {
          if (this.isEdit) {
            api.updateApplicationMatter(this.record.matter_id, data)
              .then(data => {
                this.$notification.success({message: '成功', description: '更新成功', key: 'SUCCESS'})
                this.$emit('close')
              })
          } else {
            api.createApplicationMatter(this.application_id, data)
              .then(data => {
                this.$notification.success({message: '成功', description: '新建成功', key: 'SUCCESS'})
                this.$emit('close')
              })
          }
        }
      })
    },
    handleCancel () {
      this.$emit('close')
    }

  },
  computed: {
    isEdit () {
      return this.type === 'edit'
    },
    title () {
      return this.isEdit ? '编辑' : '新建'
    }
  }
}
