import api from '../../../../api/implement'
import { mapGetters } from 'vuex'

export default {
  name: 'BuildModal',
  props: {
    record: Object,
    visible: Boolean,
    type: String,
  },
  data () {
    return {
      project_id:this.projectSelected().id,
      layout: {
        'label-col': { span: 8 },
        'wrapper-col': { span: 12 }
      },
      form: this.$form.createForm(this),
      matter: {}
    }
  },
  mounted () {
  },
  methods: {
     ...mapGetters(['projectSelected']),
    handleBuildOk () {
      this.form.validateFields((error, data) => {
        if (!error) {
          if (this.isEdit) {
            api.updateBuild(this.record.building_id, data)
              .then(data => {
                this.$notification.success({message: '成功', description: '更新成功', key: 'SUCCESS'})
                this.$emit('close')
              })
          } else {
            api.createBuild(this.project_id,data)
              .then(data => {
                this.$notification.success({message: '成功', description: '新建成功', key: 'SUCCESS'})
                this.$emit('close')
              })
          }
          this.change()
        }
      })
    },
    handleBuildCancel () {
      this.$emit('close')
    },
     validateProgressLimit (rule, value, callback) {
      if (this.isEdit) {
        callback()
      } else {
        if(value<0||value>100)
          callback('false')
        else
          callback()
      }
    }

  },
  computed: {
    isEdit () {
      return this.type === 'edit'
    },
    title () {
      return this.isEdit ? '编辑' : '列名管理'
    }
  }
}
